import React, { useState } from 'react'
import './index.css'
import logo from '../../assets/images/logo.png'
import { Link } from 'react-router-dom'
import {Formik} from 'formik';
import * as Yup from 'yup';



function change(errors,values){

  // console.log("erro",errors,"valor" ,values);

  let flag = true;

  if(errors.email){
    console.log("erro email")
    flag = false;
  }
   if(errors.password){
    console.log("erro senha")
    flag = false;
  }

  if(values.email === "" ){

    flag = false;
  }

   if(values.password === "" ){
    flag = false;
  }




  // setAut(false)
}

function Login(){



    return (

        <div className="app">


    <Formik
      initialValues={{ email: "", password:"" }}

      validationSchema={Yup.object().shape({
        email: Yup.string()
          .email()
          .required("Required"),

        password: Yup.string()
            .min(8,"min 8")
            .required("Required")
      })}
    >
      {props => {
        const {
          values,
          touched,
          errors,
          handleChange,
          handleBlur,
        } = props;
        return (
          <form>


            <label htmlFor="email" style={{ display: "block" }}>
              Email
            </label>
            <input
              id="email"
              type="text"
              value={values.email}
              onChange={handleChange}
              onBlur={handleBlur}
              className={
                errors.email && touched.email
                  ? "text-input error"
                  : "text-input"
              }
            />

            {errors.email && touched.email && (
              <div className="input-feedback">{errors.email}</div>
            )}

            <label htmlFor="password" style={{ display: "block" }}>
              Password
            </label>
            <input
              id="password"
              type="password"
              value={values.password}
              onChange={handleChange}
              onBlur={handleBlur}
              className={
                errors.password && touched.password
                  ? "text-input error"
                  : "text-input"
              }
            />

            {errors.password && touched.password && (
              <div className="input-feedback">{errors.password}</div>
            )}











            {change(errors,values)&&(
              <Link to="/users" >VAI /</Link>
            )}

            {!change(errors,values) &&(
              <Link to="/">BARRADO/</Link>
            )}








          </form>
        );
      }}
    </Formik>

  </div>
);

}

export default Login;
